var decodeBits = function(bits){
    bits = bits.replace(/^0+/, '');
    let result = '';
    let arr = [...bits];
    let buf = arr[0];
    let num = 1;
    let bitsSet = [];
    let numSet = new Set();

    for(let i =1; i < arr.length; i++){

        if(arr[i] != buf){
            bitsSet.push([buf, num])
            numSet.add(num);
            buf = arr[i];
            num = 1;     
        }
        else{
            num++;
        }
    }

    if(buf != '0'){
        bitsSet.push([buf, num])
        numSet.add(num);
    };

    let arrNum = Array.from(numSet).sort((a, b) => {
        return a - b;
      });

    bitsSet.forEach(e =>{
        console.log(e)
        if(e[0] == '1'){
            if(e[1] == arrNum[0]){
                result += ".";
            }
            if(e[1] == arrNum[1]){
                result += "-";
            }
        }
        if(e[0] == '0'){
            if(e[1] == arrNum[0]){
                result += "";
            }
            if(e[1] == arrNum[1]){
                result += " ";
            }
            if(e[1] == arrNum[2]){
                result += "   ";
            }
        }
    });

    return result;
    
    //return bits.replace('111', '-').replace('000', ' ').replace('1', '.').replace('0', '');
}

